mapper.select:
    select

    FROM admin_info tar
    WHERE  tar.is_delete =0


AND tar.id = #{id}



AND tar.user_id = #{userId}



AND tar.nick_name = #{nickName}



AND tar.role = #{role}



AND tar.cell_phone = #{cellPhone}



AND tar.create_by = #{createBy}



AND tar.create_time = #{createTime}



AND tar.update_time = #{updateTime}



AND tar.is_delete = #{isDelete}

    SELECT
    IFNULL(COUNT(tar.id),0) total
    FROM admin_info tar
    WHERE  tar.is_delete =0


AND tar.id = #{id}



AND tar.user_id = #{userId}



AND tar.nick_name = #{nickName}



AND tar.role = #{role}



AND tar.cell_phone = #{cellPhone}



AND tar.create_by = #{createBy}



AND tar.create_time = #{createTime}



AND tar.update_time = #{updateTime}



AND tar.is_delete = #{isDelete}

    select

    from admin_info tar
    WHERE  tar.is_delete =0


AND tar.id = #{id}



AND tar.user_id = #{userId}



AND tar.nick_name = #{nickName}



AND tar.role = #{role}



AND tar.cell_phone = #{cellPhone}



AND tar.create_by = #{createBy}



AND tar.create_time = #{createTime}



AND tar.update_time = #{updateTime}



AND tar.is_delete = #{isDelete}

mapper.update:
update admin_info
user_id=#{userId}, nick_name=#{nickName}, role=#{role}, cell_phone=#{cellPhone}, create_by=#{createBy}, update_time=#{updateTime}, is_delete=#{isDelete},
where id=#{id}
